import { Avatar } from "@mui/material";
import { Formik, useFormik } from "formik";
import React, { useState } from "react";
import * as Yup from "yap";
import ImageIcon from "@mui/icons-material/Image";

const validationSchema = Yup.object().shape({
  content: Yup.string().required("Tweet text is required"),
});

const HomeSection = () => {
  const [uploadingImage, setUploadingImage] = useState(false);
  const handleSubmit = (values) => {
    console.log("value ", values);
  };
  const formik = useFormik({
    initialValues: {
      content: "",
      image: "",
    },
    onSubmit: handleSubmit,
    validationSchema,
  });

  const handleSelectImage = (event) => {};

  return (
    <div className="space-y-5">
      <section>
        <h1 className="py-5 text-x1 font-bold opacity-90">Home</h1>
      </section>
      <section className={`pb-10`}>
        <div className="flex space-x-5">
          <Avatar
            alt="username"
            src="https://imgs.search.brave.com/5tgHQIyOvMT47XifWiwW0ty8fv8o-DG6SOGNvCi-Oo8/rs:fit:860:0:0/g:"
          />
          <div className="w-full">
            <form>
              <div>
                <input
                  type="text"
                  name="content"
                  placeholder="what wrong with india"
                  className={`border-none outline-none text-sl bg-transparent`}
                  {...Formik.getFieldProps("content")}
                />
                {Formik.errors.content && Formik.touched.content && (
                  <span className="text-red-500">{Formik.errors.content}</span>
                )}
              </div>
              <div>
                <img src="" alt="" />
              </div>
              <div className="flex justify-between items-center mt-5">
                <div className="flex space-x-5 items-center">
                  <ImageIcon className="text-[#1d9bf0]" />
                  <input
                    type="file"
                    name="imageFile"
                    className="hidden"
                    onChange={handleSelectImage}
                  />
                </div>
              </div>
            </form>
          </div>
        </div>
      </section>
    </div>
  );
};

export default HomeSection;
